Scripts.OnMouseDown:
if(arg1	== "LeftButton")	then
	self:StartMoving();
	self.isMoving	=	true;
end

Scripts.OnMouseUp:
if(self.isMoving) then
	self:StopMovingOrSizing();
	self.isMoving	=	false;
end

Scripts.OnClick:
PanelTemplates_Tab_OnClick(self, MetaMap_DialogFrame);
MetaMap_OptionsTab_OnClick(self);
for i = 1, 6 do
	if i == self:GetID() then
		getglobal(self:GetName().."Text"):Hide();
		getglobal(self:GetName().."HighlightText"):Hide();
		getglobal(self:GetName().."DisabledText"):Show();
	else
		getglobal("MetaMap_DialogFrameTab"..i.."Text"):Show();
		getglobal("MetaMap_DialogFrameTab"..i.."HighlightText"):Hide();
		getglobal("MetaMap_DialogFrameTab"..i.."DisabledText"):Hide();
	end
end

Scripts.OnEnter:
if(self:GetID()	== 0) then
	MetaMap_MapNoteOnEnter(0);
elseif(self:GetID()	== 1) then
	MetaMap_MapNoteOnEnter(-1);
end

Ui.Frame:
 --y was 24, make it longer for new continent

	
		
			
				
					
				
				
					
				
				
						
	
		
		
			
				
					
				
				
					
				
								
			
													
		
			
				
					
						
					
				
			
			
			
		
	


		
	
		
		
			
				
			
			
				
			
			
		
				
				
					
						
						
										
															
			
		
		
			
			
				
				
			
		
		
			
				self:SetWidth(self:GetHeight());
			
			
				local menu = getglobal(self:GetParent():GetName().."Menu")
				ToggleDropDownMenu(nil, nil, menu, self:GetParent():GetName(), 0, 0);

Scripts.OnClick:
MetaMap_ToggleOptions(self.option);
getglobal("MetaMapButtonIcon"):SetTexture(METAMAP_ICON);
if(IsAddOnLoaded("FuBar")) then
	MetaMapFu:SetIcon(METAMAP_ICON);
end
MetaMap_SetTrackIcon();

Scripts.OnEnter:
WorldMapTooltip:SetOwner(self, "ANCHOR_CENTER");
WorldMapTooltip:SetText("Tooltip Scaling Sample",	0, 1,	0);
WorldMapTooltip:AddLine("Move	the	Tooltip	slider to	change the scale of	the	tooltips shown on	the	WorldMap", 1,	1, 1,	true);
WorldMapTooltip:Show();

Scripts.OnClick:
if(not StaticPopup1:IsVisible()) then
	ToggleDropDownMenu(nil, nil, MetaMap_NotesList, self:GetName(), 0, 0);
end

Scripts.OnMouseDown:
if ( button	== "LeftButton"	)	then
	self:GetParent():StartMoving();
	self.isMoving	=	true;
end

Scripts.OnMouseUp:
if ( self.isMoving ) then
	self:GetParent():StopMovingOrSizing();
	self.isMoving	=	false;
	WorldMapFrame:SetUserPlaced(true);
end

Scripts.OnMouseDown:
if(IsShiftKeyDown() or IsControlKeyDown()) then
	MiniMapCoords_OnClick(arg1);
else
	self:StartMoving();
	self.isMoving	=	true;
end

Scripts.OnMouseUp:
if(self.isMoving) then
	self:StopMovingOrSizing();
	self.isMoving	=	false;
end

Scripts.OnTextChanged:
if(strlen(self:GetText()) > 0 and MetaMap_ToggleSendValue ~= 1) then
	MetaMap_SendPlayer:Enable();
else
	MetaMap_SendPlayer:Disable();
end

Scripts.OnLoad:
if(IsAddOnLoaded("Titan")) then
	TitanPanelMetaMapButton_OnLoad(self);
	TitanPanelButton_OnLoad(self);
	self:SetFrameLevel(self:GetFrameLevel()+5);
else
	self:Hide();
end
