		--UIDropDownMenu_SetWidth(50, this);
		getglobal(this:GetName().."HighlightTexture"):SetVertexColor(0.75, 0.75, 0.25);
		Outfitter_ListBox_OnLoad(this);
	
	
		Outfitter_ListBox_OnShow(this);

Scripts.OnEnter:
if this:GetChecked() then
	GameTooltip_AddNewbieTip(Outfitter_cAutoSwitch, 1.0, 1.0, 1.0, Outfitter_cAutoSwitchOnDescription, 1);
else
	GameTooltip_AddNewbieTip(Outfitter_cAutoSwitch, 1.0, 1.0, 1.0, Outfitter_cAutoSwitchOffDescription, 1);
end

if this:GetChecked() then
	GameTooltip_AddNewbieTip(Outfitter_cAutoSwitch, 1.0, 1.0, 1.0, Outfitter_cTooltipInfoOnDescription, 1);
else
	GameTooltip_AddNewbieTip(Outfitter_cAutoSwitch, 1.0, 1.0, 1.0, Outfitter_cTooltipInfoOffDescription, 1);
end

Scripts.OnHide:
if not PlayerTitleDropDown.titleCount or PlayerTitleDropDown.titleCount >= 1 then
	PlayerTitleDropDown:Show();
end

Scripts.OnMouseUp:
OutfitterMinimapDropDown_OnLoad();
this.ChangedValueFunc = OutfitterMinimapButton_ItemSelected;
ToggleDropDownMenu(nil, nil, this, this);

-- Hack to force the menu to position correctly.  UIDropDownMenu code
-- keeps thinking that it's off the screen and trying to reposition
-- it, which it does very poorly

OutfitterMinimapDropDown_AdjustScreenPosition(this);

PlaySound("igMainMenuOptionCheckBoxOn");
