Scripts.OnDragStop:
			  if(SmartBuffOptionsFrame:IsVisible()) then
          SMARTBUFF_Splash_ChangePos();
          SMARTBUFF_Splash_ChangeFont(1);
				  this:StopMovingOrSizing();
				end

Scripts.OnEnter:
				
				GameTooltip:SetOwner(this, "ANCHOR_LEFT");
				GameTooltip:SetText(SMARTBUFF_TITLE);			
        GameTooltip:AddLine(SMARTBUFF_MINIMAP_TT, SMARTBUFF_TTC_R, SMARTBUFF_TTC_G, SMARTBUFF_TTC_B, SMARTBUFF_TTC_A);
        GameTooltip:AppendText("");

Scripts.OnClick:
        if (arg1 == "LeftButton" and IsAltKeyDown()) then
          if (IsAddOnLoaded("SmartDebuff")) then
            SMARTDEBUFF_ToggleSF();
          end
				elseif (arg1 == "LeftButton") then
  			  SMARTBUFF_OptionsFrame_Toggle();
				elseif (arg1 == "RightButton") then
				  SMARTBUFF_OToggle();
          if(SmartBuffOptionsFrame:IsVisible()) then
            SMARTBUFF_Options_OnShow();
          end
	      end

Scripts.OnDragStart:
if (arg1 == "LeftButton" and IsShiftKeyDown()) then
  this:StartMoving();
  SmartBuff_IconDraggingFrame:Show();
end

Scripts.OnLoad:
        this:EnableMouse(true);
        this:EnableKeyboard(false);      
   	    this:RegisterForDrag("LeftButton");
        this:RegisterForClicks("LeftButtonUp", "RightButtonUp");

        
        
        this:SetScript("PreClick", SMARTBUFF_OnPreClick);
        this:SetScript("PostClick", SMARTBUFF_OnPostClick);
        
        this.title = this:CreateFontString(nil, "OVERLAY");
      	this.title:SetFontObject("GameFontNormal");
      	this.title:SetAllPoints(this);
      	this.title:SetText("SB");
        
        this:SetAttribute("type", "spell");
        this:SetAttribute("unit", nil);
        this:SetAttribute("spell", nil);
        this:SetAttribute("item", nil);
        this:SetAttribute("target-slot", nil);

Scripts.OnDragStart:
if (arg1 == "LeftButton" and IsShiftKeyDown()) then
  this:StartMoving();
end

Scripts.OnValueChanged:
			      local n = this:GetValue();
            getglobal(this:GetName().."Text"):SetText(SMARTBUFF_MSG_CHARGES .. " " .. n);
            SMARTBUFF_Options.MinCharges = n;
            if (n == 0) then
              SMARTBUFF_Options.CheckCharges = false;
            else
              SMARTBUFF_Options.CheckCharges = true;
            end

local n = this:GetValue();
getglobal(this:GetName().."Text"):SetText(string.format("Splash %.1fsec", n));
SMARTBUFF_Options.SplashDuration = n;

Scripts.OnMouseDown:
if (arg1 == "LeftButton" and IsShiftKeyDown()) then
	this:StartMoving();
end
